import './LoginSignup.css';
import React, { useState } from 'react';
import axios from 'axios';

interface LoginResponse {
  token: string;
}

const LoginSignup: React.FC = () => {
  const [email, setEmail] = useState<string>('');
  const [password, setPassword] = useState<string>('');
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState<boolean>(false);

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    try {
      const response = await axios.post<LoginResponse>('https://your-api-endpoint.com/auth/login', {
        email,
        password,
      });

      // Sauvegarder le token JWT dans localStorage
      localStorage.setItem('token', response.data.token);

      // Rediriger l'utilisateur ou effectuer une autre action après la connexion
      console.log('Login réussi, Token:', response.data.token);
      // Vous pouvez par exemple rediriger l'utilisateur vers une autre page
      // window.location.href = '/dashboard';

    } catch (err) {
      setError('Échec de la connexion. Veuillez vérifier vos informations.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Connexion</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Mot de passe:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit" disabled={loading}>
          {loading ? 'Connexion en cours...' : 'Se connecter'}
        </button>
      </form>
    </div>
  );
};

export default LoginSignup;
