import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Lesson: React.FC = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');

    // Si l'utilisateur n'est pas authentifié (pas de token), on le redirige vers la page d'accueil (/)
    if (!token) {
      navigate('/');
    }
  }, [navigate]); // Dépendance sur `navigate` pour éviter les problèmes de navigation

  return (
    <div>
      <h1>Lesson Page</h1>
      <p>This is the lesson page, but you need to be authenticated to view it.</p>
    </div>
  );
};

export default Lesson;
