import React, { useEffect, useRef, useState } from 'react';
import { HandLandmarker, FilesetResolver } from '@mediapipe/tasks-vision';
import { drawConnectors, drawLandmarks } from '@mediapipe/drawing_utils';
import { HAND_CONNECTIONS } from '@mediapipe/hands';

const HandLandmarkerComponent: React.FC = () => {
  const [handLandmarker, setHandLandmarker] = useState<HandLandmarker | undefined>(undefined);
  const [webcamRunning, setWebcamRunning] = useState<boolean>(false);
  const [runningMode, setRunningMode] = useState<'IMAGE' | 'VIDEO'>('IMAGE');
  const videoRef = useRef<HTMLVideoElement | null>(null);
  const canvasRef = useRef<HTMLCanvasElement | null>(null);
  const [lastVideoTime, setLastVideoTime] = useState<number>(-1);

  // Charger le modèle MediaPipe HandLandmarker lors du montage du composant
  useEffect(() => {
    const loadHandLandmarker = async () => {
      const vision = await FilesetResolver.forVisionTasks(
        'https://cdn.jsdelivr.net/npm/@mediapipe/tasks-vision@0.10.0/wasm'
      );
      const handLandmarkerInstance = await HandLandmarker.createFromOptions(vision, {
        baseOptions: {
          modelAssetPath: 'https://storage.googleapis.com/mediapipe-models/hand_landmarker/hand_landmarker/float16/1/hand_landmarker.task',
          delegate: 'GPU',
        },
        runningMode: runningMode,
        numHands: 2,
      });
      setHandLandmarker(handLandmarkerInstance);
    };

    loadHandLandmarker();
  }, [runningMode]);

  // Gérer l'activation de la webcam
  const enableCam = async () => {
    if (!handLandmarker) {
      console.log('Wait! HandLandmarker not loaded yet.');
      return;
    }

    if (!webcamRunning) {
      setWebcamRunning(true);

      // getUserMedia parameters.
      const constraints = { video: true };
      const stream = await navigator.mediaDevices.getUserMedia(constraints);

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.addEventListener('loadeddata', predictWebcam);
      }
    } else {
      setWebcamRunning(false);
    }
  };

  // Prédiction en temps réel avec la webcam
  const predictWebcam = async () => {
    if (!videoRef.current || !canvasRef.current || !handLandmarker) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    const canvasCtx = canvas.getContext('2d');

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    if (runningMode === 'IMAGE') {
      setRunningMode('VIDEO');
      await handLandmarker.setOptions({ runningMode: 'VIDEO' });
    }

    const startTimeMs = performance.now();
    if (lastVideoTime !== video.currentTime) {
      setLastVideoTime(video.currentTime);
      const results = handLandmarker.detectForVideo(video, startTimeMs);

      canvasCtx?.clearRect(0, 0, canvas.width, canvas.height);
      if (results.landmarks) {
        for (const landmarks of results.landmarks) {
          drawConnectors(canvasCtx, landmarks, HAND_CONNECTIONS, {
            color: '#00FF00',
            lineWidth: 5,
          });
          drawLandmarks(canvasCtx, landmarks, { color: '#FF0000', lineWidth: 2 });
        }
      }
    }

    if (webcamRunning) {
      requestAnimationFrame(predictWebcam);
    }
  };

  // Gestion des événements de clic sur les images
  const handleImageClick = async (event: React.MouseEvent<HTMLImageElement>) => {
    if (!handLandmarker || runningMode === 'VIDEO') return;

    setRunningMode('IMAGE');
    await handLandmarker.setOptions({ runningMode: 'IMAGE' });

    const image = event.target as HTMLImageElement;
    const canvas = document.createElement('canvas');
    canvas.width = image.naturalWidth;
    canvas.height = image.naturalHeight;
    const ctx = canvas.getContext('2d') as CanvasRenderingContext2D;

    document.body.appendChild(canvas);

    const results = handLandmarker.detect(image);

    if (results.landmarks) {
      for (const landmarks of results.landmarks) {
        drawConnectors(ctx, landmarks, HAND_CONNECTIONS, {
          color: '#00FF00',
          lineWidth: 5,
        });
        drawLandmarks(ctx, landmarks, { color: '#FF0000', lineWidth: 2 });
      }
    }
  };

  return (
    <div>
      <h1>Hand Landmark Detection</h1>

      {/* Images à cliquer pour détecter */}
      <section>
        <h2>Demo: Detecting Images</h2>
        <p>Click on an image to detect hand landmarks.</p>
        <div onClick={handleImageClick}>
          <img
            src="https://assets.codepen.io/9177687/hand-ge4ca13f5d_1920.jpg"
            width="400"
            alt="Hand"
          />
        </div>
        <div onClick={handleImageClick}>
          <img
            src="https://assets.codepen.io/9177687/couple-gb7cb5db4c_1920.jpg"
            width="400"
            alt="Couple"
          />
        </div>
      </section>

      {/* Webcam en direct */}
      <section>
        <h2>Demo: Webcam continuous hands landmarks detection</h2>
        <p>
          Hold your hand in front of your webcam to get real-time hand landmark detection.
        </p>
        <button onClick={enableCam} className="mdc-button mdc-button--raised">
          {webcamRunning ? 'DISABLE WEBCAM' : 'ENABLE WEBCAM'}
        </button>
        <div style={{ position: 'relative' }}>
          <video ref={videoRef} style={{ position: 'absolute' }} autoPlay playsInline></video>
          <canvas ref={canvasRef} style={{ position: 'absolute', left: 0, top: 0 }}></canvas>
        </div>
      </section>
    </div>
  );
};

export default HandLandmarkerComponent;
