import React, { useState } from 'react';
import './LoginSignup.css';

// Helper function to validate email
const isValidEmail = (email: string): boolean => {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
};

// Helper function to validate password security
const isValidPassword = (password: string): boolean => {
  // Password must be at least 8 characters long and contain at least one number and one special character
  const passwordRegex = /^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{8,}$/;
  return passwordRegex.test(password);
};

const LoginSignup: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState<{ email?: string; password?: string; apiError?: string }>({});

  // Simulated API call
  const fakeApiCall = (): Promise<string> => {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        // Simulate success or failure randomly
        Math.random() > 0.5 ? resolve('Success') : reject('API Error: Unable to process request');
      }, 1000);
    });
  };

  const handleSignUp = async () => {
    let validationErrors = {};

    // Validate email
    if (!isValidEmail(email)) {
      validationErrors = { ...validationErrors, email: 'Invalid email address' };
    }

    // Validate password
    if (!isValidPassword(password)) {
      validationErrors = {
        ...validationErrors,
        password: 'Password must be at least 8 characters long and contain at least one number and one special character',
      };
    }

    setErrors(validationErrors);

    // If there are validation errors, stop the process
    if (Object.keys(validationErrors).length > 0) return;

    try {
      setLoading(true); // Set loading state
      const response = await fetch('https://example.com/api/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to sign up');
      }

      const data = await response.json();
      console.log('Success:', data);
    } catch (error: any) {
      setErrors((prevErrors) => ({ ...prevErrors, apiError: error.message }));
    } finally {
      setLoading(false); // End loading state
    }
  };

  // Function to handle Login
  const handleLogin = async () => {
    try {
      setLoading(true); // Set loading state
      const response = await fetch('https://example.com/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to login');
      }

      const data = await response.json();
      console.log('Login success:', data);
    } catch (error: any) {
      setErrors((prevErrors) => ({ ...prevErrors, apiError: error.message }));
    } finally {
      setLoading(false); // End loading state
    }
  };

  return (
    <div className="login-signup-container">
      <h2>Connection</h2>
      <div className="form-group">
        <input
          type="email"
          placeholder="Email Id"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        {errors.email && <p className="error-message">{errors.email}</p>}
      </div>
      <div className="form-group">
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        {errors.password && <p className="error-message">{errors.password}</p>}
      </div>
      <p className="forgot-password">
        Lost Password? <a href="#">Click Here!</a>
      </p>
      <div className="button-container">
        <button onClick={handleSignUp}>Sign Up</button>
        <button onClick={handleLogin}>Login</button>
      </div>
      {errors.apiError && <p className="api-error-message">{errors.apiError}</p>}
    </div>
  );
};

export default LoginSignup;
